mapper.select:
SELECT 

FROM t_message_user a

WHERE a.id = #{id}

SELECT 

FROM t_message_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT 

FROM t_message_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE t_message_user SET 	
	message_id = #{messageId},
	receiver = #{receiver},
	group_id = #{groupId},
	read_flag = #{readFlag}
WHERE id = #{id}

UPDATE t_message_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
